package com.NhaHang.bean;

public class FoodGuestCall {
	private String tenmon;
	private double gia;
	private int soluong;
	private String trangthaigoi;
	private int stt;
	private String phanhoidaubep;
	private int magoimon;
	private int soban;
	private String thoigian;
	private int soluongchebien;
	private String yeucauthem;
	public FoodGuestCall() {
		super();
		// TODO Auto-generated constructor stub
	}
	public FoodGuestCall(String tenmon, double gia, int soluong, String trangthaigoi, int stt, String phanhoidaubep,
			int magoimon, int soban, String thoigian, int soluongchebien, String yeucauthem) {
		super();
		this.tenmon = tenmon;
		this.gia = gia;
		this.soluong = soluong;
		this.trangthaigoi = trangthaigoi;
		this.stt = stt;
		this.phanhoidaubep = phanhoidaubep;
		this.magoimon = magoimon;
		this.soban = soban;
		this.thoigian = thoigian;
		this.soluongchebien = soluongchebien;
		this.yeucauthem = yeucauthem;
	}
	public String getTenmon() {
		return tenmon;
	}
	public void setTenmon(String tenmon) {
		this.tenmon = tenmon;
	}
	public double getGia() {
		return gia;
	}
	public void setGia(double gia) {
		this.gia = gia;
	}
	public int getSoluong() {
		return soluong;
	}
	public void setSoluong(int soluong) {
		this.soluong = soluong;
	}
	public String getTrangthaigoi() {
		return trangthaigoi;
	}
	public void setTrangthaigoi(String trangthaigoi) {
		this.trangthaigoi = trangthaigoi;
	}
	public int getStt() {
		return stt;
	}
	public void setStt(int stt) {
		this.stt = stt;
	}
	public String getPhanhoidaubep() {
		return phanhoidaubep;
	}
	public void setPhanhoidaubep(String phanhoidaubep) {
		this.phanhoidaubep = phanhoidaubep;
	}
	public int getMagoimon() {
		return magoimon;
	}
	public void setMagoimon(int magoimon) {
		this.magoimon = magoimon;
	}
	public int getSoban() {
		return soban;
	}
	public void setSoban(int soban) {
		this.soban = soban;
	}
	public String getThoigian() {
		return thoigian;
	}
	public void setThoigian(String thoigian) {
		this.thoigian = thoigian;
	}
	public int getSoluongchebien() {
		return soluongchebien;
	}
	public void setSoluongchebien(int soluongchebien) {
		this.soluongchebien = soluongchebien;
	}
	public String getYeucauthem() {
		return yeucauthem;
	}
	public void setYeucauthem(String yeucauthem) {
		this.yeucauthem = yeucauthem;
	}
	
}
